import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import Button from "../../Components/Button/Button";
import ContactForm from "../Content/ContactForm"; // New component for the modal form
import { Pulse, Bounce, Reveal } from "react-reveal";
import coder from "../../Assets/Images/coder.gif";
import githubLink from "../../Assets/Images/github-link.svg";
import twitterLink from "../../Assets/Images/twitter-link.svg";
import linkedinLink from "../../Assets/Images/linkedin-link.svg";

import Marquee from "react-fast-marquee";

const Header = () => {
  const [hovered, setHovered] = useState(false);
  const [showModal, setShowModal] = useState(false); // New state to control the modal visibility

  const handleHover = () => {
    setHovered(true);
  };

  const handleLeave = () => {
    setHovered(false);
  };

  const handleContactClick = () => {
    setShowModal(true);
  };

  const handleModalClose = () => {
    setShowModal(false);
  };

  return (
    <React.Fragment>
      <header className="min-h-screen w-full">
        <nav className="flex flex-col sm:flex-row justify-between px-4 sm:px-8 md:px-16 lg:px-24 xl:px-32 py-4 bg-gray-900 text-white sticky top-0 w-full z-50">
          {/* ... other navigation items ... */}

          <div className="flex gap-4 sm:gap-8 items-center mt-4 sm:mt-0">
            {/* ... other NavLink items ... */}

            {/* Contact NavLink with onClick event to show modal */}
            <NavLink
              to="#"
              className="block text-base hover:text-[#F7B32B] transition-colors"
              activeClassName="text-[#F7B32B]"
              onClick={handleContactClick}
            >
              Contact
            </NavLink>
          </div>

          <div className="flex gap-4 sm:gap-8 items-center mt-4 sm:mt-0">
            {/* ... social media icons ... */}
          </div>
        </nav>

        {/* ... other content ... */}

        {/* Modal Form */}
        {showModal && (
          <div className="fixed top-0 left-0 w-full h-full bg-gray-800 bg-opacity-75 flex justify-center items-center z-50">
            <ContactForm onClose={handleModalClose} />
          </div>
        )}
      </header>
    </React.Fragment>
  );
};

export default Header;
