import React from "react";
import Button from "../../Components/Button/Button";

const Contact = ({ onClose }) => {
  // Implement the logic for sending the email using your email js account here.

  // Placeholder event handler for form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Here, you can implement the logic for sending the email using your email js account.
    // Refer to the email js documentation for how to integrate it properly.
    // Once the email is sent, you can close the modal by calling `onClose()`.
    onClose();
  };

  return (
    <div className="bg-white rounded-lg p-8">
      <h2 className="text-2xl font-bold mb-4">Contact Us</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="name" className="block font-medium mb-2">
            Name
          </label>
          <input
            type="text"
            id="name"
            name="name"
            className="w-full border border-gray-300 rounded-md py-2 px-4"
            required
          />
        </div>
        <div>
          <label htmlFor="email" className="block font-medium mb-2">
            Email
          </label>
          <input
            type="email"
            id="email"
            name="email"
            className="w-full border border-gray-300 rounded-md py-2 px-4"
            required
          />
        </div>
        <div>
          <label htmlFor="message" className="block font-medium mb-2">
            Message
          </label>
          <textarea
            id="message"
            name="message"
            rows="4"
            className="w-full border border-gray-300 rounded-md py-2 px-4 resize-none"
            required
          ></textarea>
        </div>
        <Button type="black" text="Send Email" className="w-full sm:w-auto" />
        <Button
          type="white"
          text="Cancel"
          className="w-full sm:w-auto"
          onClick={onClose}
        />
      </form>
    </div>
  );
};

export default Contact;
