import React, { useState } from 'react';
import emailjs from 'emailjs-com';

const Contact = () => {
  const [showPopup, setShowPopup] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Use your EmailJS service ID, template ID, and user ID
    const serviceId = 'service_6rhoez3';
    const templateId = 'template_su0puwl';
    const userId = 'YOUR_EMAILJS_USER_ID';

    emailjs.sendForm(serviceId, templateId, formData, userId)
      .then((result) => {
        console.log('Message sent successfully!', result.text);
        // You can show a success message or close the popup here
      })
      .catch((error) => {
        console.error('Error sending the message:', error);
        // Handle the error, maybe show an error message to the user
      });
  };

  return (
    <>
      <button onClick={() => setShowPopup(true)}>Contact Us</button>
      {showPopup && (
        <div className="modal">
          <form onSubmit={handleSubmit}>
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
              />
            </label>
            <label>
              Email:
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
              />
            </label>
            <label>
              Message:
              <textarea
                name="message"
                value={formData.message}
                onChange={handleInputChange}
              />
            </label>
            <button type="submit">Send</button>
          </form>
        </div>
      )}
    </>
  );
};

export default Contact;
